package assignment_javaprograms;
class bitly{
	private String Un="Saraswathi";
	private int mo=123;
	public String getUn() {
		return Un;
	}
	public void setUn(String Un) {
		this.Un=Un;}
		public int getmo() {
			return mo;
		}
		public void setun(int mo) {
			this.mo=mo;
	}
		
}

public class Encapsulation_ {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		bitly a=new bitly();
		a.setUn("Bhavani");
		System.out.println(a.getUn());
		a.setun(156);
		System.out.println( a.getmo());                                   
	}}
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		

	
